import React, { useState } from "react";
import Editor from "./Editor";

function App() {

  const [html, setHtml] = useState(`<center>
  <img src="https://cutt.ly/JbMvJKT" />
  <h1>I am</h1>
  <h2>Hello</h2>
</center>`);
  const [css, setCss] = useState(`h1 {
    font-family: sans-serif,
    color: blue
  }
h2 {
    color: red
  }
img {
    width: 200px;
    height: auto;
    border-radius: 20px;
  }`);

  const srcDoc = `
  <html>
    <body>${html}<body>
    <style>${css}<style>
  <html>
  `


  return (
    <>
      <main className="py-12 px-20">
        <h1 className="text-center text-3xl font-bold mb-4">Code Editor</h1>
        <h5 className="text-center text-xl">You can write and preview HTML and CSS!</h5>
        <section className="flex my-8">
          <div className="flex-50">
            < Editor language="xml" displayName="html" value={html} onChange={setHtml} />
            < Editor language="css" displayName="css" value={css} onChange={setCss} />
          </div>
          <div className="flex-50">
            < iframe
              srcDoc={srcDoc}
              title="output"
              sandbox="allow-scripts"
              frameBorder="0"
              width="100%"
              height="100%"

            />
          </div>
        </section>
      </main>
    </>
  )
}


export default App;
