import React from "react";
import Editor from "./Editor";

class App extends React.Component {

  state = {
    html: "",
    css: ""
  }

  setHtml = (value) => {
    this.setState({ html: value })
  }

  setCSS = (value) => {
    this.setState({ css: value });
  }

  render() {
    return (
      <>
        <main className="p-12">
          <h1 className="text-center text-3xl font-bold">Code Editor</h1>
          <h5 className="text-center">You can write and preview HTML and CSS!</h5>
          <section>
            <div>
              < Editor language="xml" displayName="html" value={this.state.html} onChange={this.setHtml} />
              < Editor language="css" displayName="css" value={this.state.css} onChange={this.setCSS} />
            </div>
            <div>
              < iframe
                title="output"
                sandbox="allow-scripts"
                frameBorder="0"
                width="100%"
                height="100%"
              />
            </div>
          </section>
        </main>
      </>
    )
  }
}


export default App;
