import React from "react";
import Editor from "./Editor";

class App extends React.Component {

  state = {
    html: "",
    css: ""
  }

  setHtml = (value) => {
    this.setState({ html: value })
  }

  setCSS = (value) => {
    this.setState({ css: value });
  }
  render() {
    return (
      <>
        <div>
          < Editor language="xml" displayName="html" value={this.state.html} onChange={this.setHtml} />
          < Editor language="css" displayName="css" value={this.state.html} onChange={this.setCSS} />
        </div>
        <div>
          < iframe
            title="output"
            sandbox="allow-scripts"
            frameBorder="0"
            width="100%"
            height="100%"
          />
        </div>
      </>
    )
  }
}


export default App;
