import { render } from "@testing-library/react";
import React, { useState, useEffect } from "react";
import Editor from "./Editor";

class App extends React.Component{

  state = {
    html: `<center>
  <img src="https://www.rd.com/wp-content/uploads/2021/03/GettyImages-1133605325-scaled-e1617227898456.jpg" />
  <h1>Hello World!</h1>
</center>`,
  css: `h1 {
  font-family: -apple-system, BlinkMacSystemFont, 
  'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 
  'Open Sans', 'Helvetica Neue', sans-serif;
}
img {
  width: 200px;
  height: auto;
  border-radius: 20px;
}`
  }

  setHtml = (value) => {
    this.setState({html: value})
  }

  setCss = (value) => {
    this.setState({css: value});
  }


  render() {
    let {html, css} = this.state;
    const srcDoc = ` 
     <html>
    <body>${html}<body>
    <style>${css}<style>
  <html>`;

    return (
      <>
        <main className="pt-12 px-8 sm:px-20 pb-24 bg-blue-400">
          <h1 className="text-center text-3xl font-bold mb-4">Code Editor</h1>
          <h5 className="text-center text-xl">You can write and preview HTML and CSS!</h5>
          <section className="flex my-8 flex-col lg:flex-row lg:justify-between">
            <div className="flex-50">
              < Editor language="xml" displayName="html" value={html} onChange={this.setHtml} />
              < Editor language="css" displayName="css" value={css} onChange={this.setCss} />
            </div>
            <div className="flex-50">
              <div className="bg-gray-500 text-white py-2 flex justify-between px-4 rounded-t-lg">
                <div className="flex items-center">
                  <div className="mx-1 w-3 h-3 bg-red-500 rounded-full"></div>
                  <div className="mx-1 w-3 h-3 bg-yellow-400 rounded-full"></div>
                  <div className="mx-1 w-3 h-3 bg-green-500 rounded-full"></div>
                </div>
                <h4 className="font-semibold">Output</h4>
              </div>
              < iframe
                srcDoc={srcDoc}
                title="output"
                sandbox="allow-scripts"
                frameBorder="0"
                width="100%"
                height="100%"
                className="bg-gray-300 rounded-b-lg p-6"
              />
            </div>
          </section>
        </main>
      </>
    )
  }
}


export default App;
