import React from "react";
import Editor from "./Editor";

class App extends React.Component {

  state = {
    html: `<center>
  <img src="https://cutt.ly/JbMvJKT" alt="img"/>
  <h1>Hello World!</h1>
</center> `,
    css: `h1 {
 font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
}
img {
  width: 200px;
  height: auto;
  border-radius: 20px;
}`
  }

  setHtml = (value) => {
    this.setState({ html: value })
  }

  setCSS = (value) => {
    this.setState({ css: value });
  }


  render() {
    let srcDoc = `
      <html>
          <body${this.state.html}></body>
          <style>${this.state.css}</style>
      </html>
    `
    return (
      <>
        <main className="py-12 px-20">
          <h1 className="text-center text-3xl font-bold mb-4">Code Editor</h1>
          <h5 className="text-center text-xl">You can write and preview HTML and CSS!</h5>
          <section className="flex my-8">
            <div className="flex-50">
              < Editor language="xml" displayName="html" value={this.state.html} onChange={this.setHtml} />
              < Editor language="css" displayName="css" value={this.state.css} onChange={this.setCSS} />
            </div>
            <div className="flex-50">
              < iframe
                srcDoc={srcDoc}
                title="output"
                sandbox="allow-scripts"
                frameBorder="0"
                width="100%"
                height="100%"

              />
            </div>
          </section>
        </main>
      </>
    )
  }
}


export default App;
